import React from "react";
import ReactJson from "react-json-view";

const ResultViewer = ({ result }) => {
  // Helper: render table rows for main fields
  const fields = [
    { label: "Name", key: "name" },
    { label: "Phone", key: "phone" },
    { label: "Email", key: "email" },
    { label: "Address", key: "address" },
    { label: "Postcode", key: "postcode" },
    { label: "Date of Birth", key: "dob" },
  ];

  return (
    <div className="mt-6 bg-white p-6 rounded-xl shadow-md">
      <div className="flex items-center justify-between">
        <h2 className="text-lg font-semibold text-gray-800">Extracted Data</h2>
      </div>

      {/* Table view for structured fields
      <div className="overflow-x-auto mt-4">
        <table className="min-w-full border text-sm">
          <tbody>
            {fields.map(({ label, key }) => (
              <tr key={key} className="border-b last:border-b-0">
                <td className="font-medium px-2 py-1 bg-gray-50 w-32">{label}</td>
                <td className="px-2 py-1">{result?.[key] ?? <span className="text-gray-400">—</span>}</td>
              </tr>
            ))}
            <tr>
              <td className="font-medium px-2 py-1 bg-gray-50">OCR Text</td>
              <td className="px-2 py-1 whitespace-pre-line max-w-lg break-words">{result?.ocrText || <span className="text-gray-400">—</span>}</td>
            </tr>
          </tbody>
        </table>
      </div> */}

      {/* JSON view for advanced users */}
      <div className="mt-6 text-sm text-gray-700">
        <div className="mb-2 font-semibold text-gray-600">Raw JSON</div>
        <ReactJson src={result} name={null} collapsed={1} enableClipboard={false} displayDataTypes={false} />
      </div>
    </div>
  );
};

export default ResultViewer;
